    // 将类似 "\345\245\275" 的字符串转为 byte[]
    public static byte[] decodeOctalString(String input) {
        String[] parts = input.split("\\\\");  // 按照反斜杠分割
        byte[] bytes = new byte[parts.length - 1];  // 第一个元素是空的

        for (int i = 1; i < parts.length; i++) {
            // 将每一段转为 byte（八进制数）
            int value = Integer.parseInt(parts[i], 8);
            bytes[i - 1] = (byte) value;
        }

        return bytes;
    }

